--
-- Name: update_user_badge(); Type: FUNCTION; Schema: public; Owner: -
--

CREATE FUNCTION public.update_user_badge() RETURNS void
    LANGUAGE plpgsql
    AS $$
DECLARE
  user_record RECORD;
  new_badge TEXT;
BEGIN
  FOR user_record IN SELECT * FROM user_total_xp LOOP
    -- Determine badge from XP range
    IF user_record.total_xp <= 500 THEN
      new_badge := 'T5';
    ELSIF user_record.total_xp <= 1100 THEN
      new_badge := 'T4';
    ELSIF user_record.total_xp <= 2000 THEN
      new_badge := 'T3';
    ELSIF user_record.total_xp <= 2700 THEN
      new_badge := 'T2';
    ELSE
      new_badge := 'T1';
    END IF;

    -- Add badge if not already present
    UPDATE users
    SET badges = 
      CASE 
        WHEN NOT (new_badge = ANY(badges)) 
        THEN badges || new_badge 
        ELSE badges 
      END
    WHERE email = user_record.email;
  END LOOP;
END;
$$;


--
-- Name: update_user_badges(); Type: FUNCTION; Schema: public; Owner: -
