CREATE FUNCTION public.update_user_badges() RETURNS void
    LANGUAGE plpgsql
    AS $$
DECLARE
  user_record RECORD;
  new_badge TEXT;
BEGIN
  FOR user_record IN SELECT * FROM user_total_xp LOOP
    -- Determine badge from XP range
    IF user_record.total_xp <= 500 THEN
      new_badge := 'T5';
    ELSIF user_record.total_xp <= 1100 THEN
      new_badge := 'T4';
    ELSIF user_record.total_xp <= 2000 THEN
      new_badge := 'T3';
    ELSIF user_record.total_xp <= 2700 THEN
      new_badge := 'T2';
    ELSE
      new_badge := 'T1';
    END IF;

    -- Overwrite badge to keep only the most recent one
    UPDATE users
    SET badges = ARRAY[new_badge]
    WHERE email = user_record.email;
  END LOOP;
END;
$$;


